 namespace tk\GuzzleHttp\Exception; use tk\Psr\Http\Client\NetworkExceptionInterface; use tk\Psr\Http\Message\RequestInterface; class ConnectException extends \tk\GuzzleHttp\Exception\TransferException implements \tk\Psr\Http\Client\NetworkExceptionInterface { private $request; private $handlerContext; public function __construct(string $message, \tk\Psr\Http\Message\RequestInterface $request, \Throwable $previous = null, array $handlerContext = []) { parent::__construct($message, 0, $previous); $this->request = $request; $this->handlerContext = $handlerContext; } public function getRequest() : \tk\Psr\Http\Message\RequestInterface { return $this->request; } public function getHandlerContext() : array { return $this->handlerContext; } } 