<?php
 namespace tk\GuzzleHttp; function describe_type($input) : string { return \tk\GuzzleHttp\Utils::describeType($input); } function headers_from_lines(iterable $lines) : array { return \tk\GuzzleHttp\Utils::headersFromLines($lines); } function debug_resource($value = null) { return \tk\GuzzleHttp\Utils::debugResource($value); } function choose_handler() : callable { return \tk\GuzzleHttp\Utils::chooseHandler(); } function default_user_agent() : string { return \tk\GuzzleHttp\Utils::defaultUserAgent(); } function default_ca_bundle() : string { return \tk\GuzzleHttp\Utils::defaultCaBundle(); } function normalize_header_keys(array $headers) : array { return \tk\GuzzleHttp\Utils::normalizeHeaderKeys($headers); } function is_host_in_noproxy(string $host, array $noProxyArray) : bool { return \tk\GuzzleHttp\Utils::isHostInNoProxy($host, $noProxyArray); } function json_decode(string $json, bool $assoc = \false, int $depth = 512, int $options = 0) { return \tk\GuzzleHttp\Utils::jsonDecode($json, $assoc, $depth, $options); } function json_encode($value, int $options = 0, int $depth = 512) : string { return \tk\GuzzleHttp\Utils::jsonEncode($value, $options, $depth); } 